import App from "next/app";
import { withApollo } from "./../lib/apollo";
import { ApolloProvider } from "@apollo/client";
import { FC, useState, useEffect } from "react";
import { useAllFilmsQuery } from "../generated/graphql";

const Layout = ({ children }) => (
  <div className="layout">
    <nav>
      <h3>SWAPI</h3>
      <label htmlFor="type">Type: &nbsp;</label>
      <select>
        <option value="movie">Movie</option>
        <option value="character">Character</option>
        <option value="droid">droid</option>
      </select>
    </nav>
    {children}
  </div>
);

const MyApp = ({ Component, pageProps, apolloClient }) => {
  const [all, setAll] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const movies = useAllFilmsQuery();

  useEffect(() => {
    if (!movies.loading && !movies.error && movies.data)
      setAll(prevAll => {
        return [...prevAll, movies.data];
      });
  }, [movies.loading]);
  return (
    <ApolloProvider client={apolloClient}>
      <Layout>
        <Component {...pageProps} />
      </Layout>
    </ApolloProvider>
  );
};

export default withApollo(MyApp);
