import React, { useState, useEffect, useReducer } from 'react'
import { useAllFilmsLazyQuery, useAllCharacterNamesLazyQuery, useAllStarshipsLazyQuery, useAllStarshipsQuery, useAllCharacterNamesQuery, useAllFilmsQuery } from '../generated/graphql'

const initState = {
  isLoading: true,
  mainType: "films",
  typeArr: []
}

const reducer = (state, action) => {
  switch (action.type) {
    case 'setLoading':
      return { ...state, isLoading: true }
    case 'setType':
      return { ...state, mainType: action.payload }
    case 'setArr':
      return { ...state, typeArr: action.payload, isLoading: false }
    default:
      return []
  }
}

export const useTypeFilter = () => {
  const [state, dispatch] = useReducer(reducer, initState)
  const films = useAllFilmsQuery()
  const persons = useAllCharacterNamesQuery()
  const starships = useAllStarshipsQuery()

  useEffect(() => {
    if (state.mainType === "films") {
      if (films.loading) {
        dispatch({ type: 'setLoading' })
      } else if (!films.error && films.data) {
        dispatch({ type: 'setArr', payload: films.data.allFilms })
      }
    }
  }, [films])

  useEffect(() => {
    if (state.mainType === "persons") {
      if (!persons.loading && !persons.error && persons.data) {
        dispatch({ type: 'typeArr', payload: persons.data.allPersons })
      }
    }
  }, [persons])

  useEffect(() => {
    if (state.mainType === "starships") {
      if (!starships.loading && !starships.error && starships.data) {
        dispatch({ type: 'typeArr', payload: starships.data.allStarships })
      }
    }
  }, [starships])


  return [state, dispatch]
}
