import React, { FC } from "react";
import { useTypeFilter } from "../../hooks/useTypeFilter";
import withData from "../../HOC/withData";

const Layout: FC<any> = ({ state, dispatch, children }) => {
  // const [state, dispatch] = useTypeFilter();

  const handleSelect = e => {
    e.preventDefault();
    dispatch({ type: "setType", payload: e.target.value });
    dispatch({ type: "setArr", payload: [] });
  };

  return (
    <div className="layout">
      <nav>
        <h3>SWAPI</h3>
        <label htmlFor="type">Type: &nbsp;</label>
        <select defaultValue={state.mainType} onChange={e => handleSelect(e)}>
          <option value="films">Movie</option>
          <option value="persons">Characters</option>
          <option value="droid">Starships</option>
        </select>
      </nav>
      {children}
    </div>
  );
};

export default Layout;
