import React, { useState, useEffect, useReducer } from 'react'
import { useAllFilmsQuery, useAllCharacterNamesQuery, useAllStarshipsQuery, useAllFilmsLazyQuery, useAllCharacterNamesLazyQuery, useAllStarshipsLazyQuery } from '../generated/graphql'

const initState = {
  mainType: "movies"
}

const reducer = (state, action) => {
  switch (action.type) {
    case 'setType':
      return { ...state, mainType: action.payload }
    default:
      return []
  }
}

export const useTypeFilter = () => {
  const [state, dispatch] = useReducer(reducer, initState)
  const movies = useAllFilmsLazyQuery()
  const persons = useAllCharacterNamesLazyQuery()
  const starships = useAllStarshipsLazyQuery()

  useEffect(() => {

  })


  return [state, dispatch]
}
