import React, { useState, useEffect, useReducer } from 'react'
import { useAllFilmsQuery, useAllCharacterNamesQuery, useAllStarshipsQuery } from '../generated/graphql'

const initState = []

const reducer = (state, action) => {
  switch (action.type) {
    case 'setType':
      return [...state, action.payload]
    default:
      return []
  }
}

export const useTypeFilter = () => {
  const [state, dispatch] = useReducer(reducer, initState)
  const movies = useAllFilmsQuery()
  const persons = useAllCharacterNamesQuery()
  const starships = useAllStarshipsQuery()

  useEffect(() => {
    dispatch({ type: 'setType', payload: movies.data.allFilms })
  }, [state])

  return [state, dispatch]
}
