import App from "next/app";
import { withApollo } from "./../lib/apollo";
import { ApolloProvider } from "@apollo/client";
import { FC, useState, useEffect } from "react";
import { useAllFilmsQuery } from "../generated/graphql";
import { useTypeFilter } from "../hooks/useTypeFilter";

const Layout = ({ children }) => {
  const [state, dispatch] = useTypeFilter();

  const handleSelect = e => {
    e.preventDefault();
    dispatch({ type: "setType", payload: e.target.value });
    dispatch({ type: "setArr", payload: [] });
  };

  return (
    <div className="layout">
      <nav>
        <h3>SWAPI</h3>
        <label htmlFor="type">Type: &nbsp;</label>
        <select defaultValue={state.mainType} onChange={e => handleSelect(e)}>
          <option value="films">Movie</option>
          <option value="persons">Characters</option>
          <option value="droid">Starships</option>
        </select>
      </nav>
      {children}
    </div>
  );
};
const Store: FC<any> = ({ Component, pageProps }) => {
  return (
    <Layout>
      <Component {...pageProps} />
    </Layout>
  );
};

const MyApp = ({ Component, pageProps, apolloClient }) => {
  return (
    <ApolloProvider client={apolloClient}>
      {/* <Store Component={Component} pageProps={pageProps} /> */}
      <Layout>
        <Component {...pageProps} />
      </Layout>
    </ApolloProvider>
  );
};

export default withApollo(MyApp);
