import React, { FC, useEffect } from "react";
import { withApollo } from "../lib/apollo";

import gql from "graphql-tag";
import { useQuery } from "@apollo/client";
import { useTypeFilter } from "../hooks/useTypeFilter";
import { useAllFilmsQuery } from "../generated/graphql";
import withData from "../HOC/withData";
import Layout from "../components/layouts/layout";
import Item from "../components/item";

const Landing: FC<any> = props => {
  const [state, dispatch] = useTypeFilter();

  useEffect(() => {}, [state, dispatch]);

  return (
    <Layout state={state} dispatch={dispatch}>
      <h1>Currently browsing {state.mainType}</h1>
      {state.isLoading && <h2>Loading...</h2>}
      {state.typeArr.map(x => (
        <Item name={!x.title ? x.name : x.title} key={x.id} />
      ))}
    </Layout>
  );
};

export default withData(Landing);
